import { ContractGroup } from '@hover-labs/kolibri-js'
import BigNumber from 'bignumber.js'

/** Represents a configuration for a network deploy */
type NetworkConfig = {
    // Name of the network
    name: string

    // URL for the tezos node
    tezosNodeUrl: string

    // URL for better call dev
    betterCallDevUrl: string

    // Number of confirmations for operations
    requiredConfirmations: number

    // Maximum retries to use when polling for confirmation
    maxConfirmationPollingRetries: number

    // Number of seconds for an operation to be included.
    operationDelaySecs: number

    // The contracts to use for deployment
    contracts: ContractGroup

    // The escrow amount to submit a governance proposal
    escrowAmount: number

    // The length of a governance vote, in blocks
    governanceVoteLength: number

    // The length of the governance timelock, in blocks
    governanceTimelockLength: number
}

export default NetworkConfig